import React from 'react';
import './Body.css';



class Body extends React.Component {
    render(){
        if (this.props.thirdSection != ''){
            this.props.thirdDash = null;
        }
        return(
            <div className="content">
                <h2 className="sectionTitle">{this.props.title}</h2>
                <h2 className="dash" style={{color: this.props.dashColor}}>—</h2>
                <div className="mainBody">
                    <p className="intro">{this.props.intro}</p>
                    <p className="para">{this.props.first}</p>
                    <img className="images" src={this.props.firstImg}/>
                    <h2 className="sectionTitle">{this.props.secondSection}</h2>
                    <h2 className="dash" style={{color: this.props.dashColor}}>—</h2>
                    <p className="para">{this.props.second}</p>
                    <p className="para">{this.props.addSecond}</p>
                    <h2 className="sectionTitle">{this.props.thirdSection}</h2>
                    <h2 className="dash" style={{color: this.props.thirdDash}}>—</h2>
                    <p className="para">{this.props.third}</p>
                </div>
                
            </div>
        )
    }
}

export default Body;
